import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const Teams = () => {
  const [teams, setTeams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const { isAuthenticated } = useAuth();

  useEffect(() => {
    fetchTeams();
  }, []);

  const fetchTeams = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/teams');
      setTeams(response.data);
    } catch (error) {
      console.error('Error fetching teams:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Open': return 'bg-green-100 text-green-600';
      case 'In Progress': return 'bg-blue-100 text-blue-600';
      case 'Completed': return 'bg-purple-100 text-purple-600';
      case 'Closed': return 'bg-gray-100 text-gray-600';
      default: return 'bg-gray-100 text-gray-600';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Find Teams</h1>
          <p className="text-gray-600">Join existing teams or create your own</p>
        </div>

        {loading ? (
          <div className="flex justify-center items-center py-12">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
          </div>
        ) : (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {teams.length === 0 ? (
              <div className="col-span-full text-center py-12">
                <p className="text-gray-500 text-lg">No teams found. Be the first to create one!</p>
              </div>
            ) : (
              teams.map(team => (
                <div key={team._id} className="bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow p-6">
                  <div className="flex justify-between items-start mb-3">
                    <h3 className="text-lg font-semibold text-gray-900">{team.teamName}</h3>
                    <span className={`px-2 py-1 rounded text-sm font-medium ${getStatusColor(team.status)}`}>
                      {team.status}
                    </span>
                  </div>
                  
                  {team.project && (
                    <div className="mb-3">
                      <p className="text-sm text-gray-500">Project:</p>
                      <p className="font-medium text-gray-900">{team.project.title}</p>
                      <span className="bg-primary-100 text-primary-600 px-2 py-1 rounded text-xs">
                        {team.project.domain}
                      </span>
                    </div>
                  )}
                  
                  <p className="text-gray-600 text-sm mb-4">{team.description}</p>
                  
                  <div className="mb-4">
                    <p className="text-xs text-gray-500 mb-2">
                      Members ({team.members?.length || 0}/{team.maxMembers || 5}):
                    </p>
                    <div className="flex flex-wrap gap-1">
                      {team.members?.slice(0, 3).map(member => (
                        <span key={member._id} className="bg-gray-100 text-gray-600 px-2 py-1 rounded text-xs">
                          {member.user?.name || 'Member'}
                        </span>
                      ))}
                      {team.members?.length > 3 && (
                        <span className="text-gray-500 text-xs">+{team.members.length - 3} more</span>
                      )}
                    </div>
                  </div>
                  
                  {team.rolesNeeded && team.rolesNeeded.length > 0 && (
                    <div className="mb-4">
                      <p className="text-xs text-gray-500 mb-2">Open Roles:</p>
                      <div className="flex flex-wrap gap-1">
                        {team.rolesNeeded.filter(role => !role.filled).slice(0, 2).map((role, index) => (
                          <span key={index} className="bg-yellow-100 text-yellow-600 px-2 py-1 rounded text-xs">
                            {role.role}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                  
                  <div className="flex justify-between items-center">
                    <button className="text-primary-600 hover:text-primary-700 font-medium text-sm">
                      View Details
                    </button>
                    <button className="bg-primary-600 text-white px-4 py-2 rounded-md hover:bg-primary-700 transition-colors text-sm">
                      Request to Join
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Teams;
